import React, { useState, useEffect } from "react";
import axios from "axios";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";

import "../CSS/UserHome.css"; // Custom CSS file for styling

const Home = () => {
  const [searchQuery, setSearchQuery] = useState(""); // State for search input
  const [searchResults, setSearchResults] = useState([]); // State for search results
  const [popularBooks, setPopularBooks] = useState([]); // State for popular books
  const [categories, setCategories] = useState([]); // State for categories
  const [writers, setWriters] = useState([]); // State for writers
  const [loading, setLoading] = useState(false); // State for loading
  const [favorites, setFavorites] = useState(new Set()); // Track favorites

  const GOOGLE_BOOKS_API_KEY = "AIzaSyBYNIOQ0-2vwqQSUEntsng8EGa0L5FaVg0"; // Your API key

  // Fetch popular books, categories, and writers on component mount
  useEffect(() => {
    setLoading(true);

    // Fetch popular books (best selling)
    axios
      .get(
        `https://www.googleapis.com/books/v1/volumes?q=best+selling&key=${GOOGLE_BOOKS_API_KEY}`
      )
      .then((response) => {
        setPopularBooks(response.data.items);
      })
      .catch((error) => console.error("Error fetching popular books:", error))
      .finally(() => setLoading(false));

    // Fetch categories (fiction as an example)
    axios
      .get(
        `https://www.googleapis.com/books/v1/volumes?q=subject:fiction&key=${GOOGLE_BOOKS_API_KEY}`
      )
      .then((response) => {
        const allCategories = response.data.items
          .map((item) => item.volumeInfo.categories || [])
          .flat();
        setCategories([...new Set(allCategories)]); // Remove duplicates
      })
      .catch((error) => console.error("Error fetching categories:", error));

    // Fetch popular writers
    axios
      .get(
        `https://www.googleapis.com/books/v1/volumes?q=subject:authors&key=${GOOGLE_BOOKS_API_KEY}`
      )
      .then((response) => {
        setWriters(response.data.items.slice(0, 5)); // Show only 5 writers
      })
      .catch((error) => console.error("Error fetching writers:", error));
  }, []);

  // Search books based on the query
  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim() === "") return;

    setLoading(true);
    setSearchResults([]); // Clear previous search results before making a new request
    axios
      .get(
        `https://www.googleapis.com/books/v1/volumes?q=${searchQuery}&key=${GOOGLE_BOOKS_API_KEY}`
      )
      .then((response) => {
        setSearchResults(response.data.items);
      })
      .catch((error) => console.error("Error searching books:", error))
      .finally(() => setLoading(false));
  };

  // Add book to favorites
  const handleAddToFavorites = (book) => {
    const newFavorites = new Set(favorites);
    if (newFavorites.has(book.id)) {
      newFavorites.delete(book.id); // Remove from favorites
    } else {
      newFavorites.add(book.id); // Add to favorites
    }
    setFavorites(newFavorites);
  };

  return (
    <div
      style={{
        backgroundColor: "#F4F1EA",
        backgroundImage: `url("https://asset.cloudinary.com/dspn3cbog/f254603dcd70f96694a355343de29489")`,
        backgroundSize: "cover",
        backgroundRepeat: "no-repeat",
        minHeight: "100vh",
        color: "#3C3C3C",
      }}
    >
      <Navbar />
      <div className="home">
        <div className="welcome-section">
          <h1>Welcome to the Book Store</h1>
          <p>Discover your next favorite book!</p>
        </div>

        {/* Search Bar */}
        <form onSubmit={handleSearch} className="search-form">
          <input
            type="text"
            placeholder="Search books by title, author, or genre..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="search-input"
          />
          <button
            type="submit"
            className="search-button"
            style={{ backgroundColor: "#C19A6B", color: "#FFFFFF" }}
            disabled={loading}
          >
            {loading ? "Searching..." : "Search"}
          </button>
        </form>

        {/* Loading Indicator */}
        {loading && <div className="loading">Loading...</div>}

        {/* Writers Section */}
        <div className="writers-section">
          <h2>Featured Writers</h2>
          <div className="writer-list">
            {writers.map((writer) => (
              <div key={writer.id} className="writer-card">
                <img
                  src={writer.volumeInfo.imageLinks?.thumbnail}
                  alt={writer.volumeInfo.title}
                  className="writer-image"
                />
                <h3>{writer.volumeInfo.title}</h3>
                <p>{writer.volumeInfo.authors?.join(", ")}</p>
              </div>
            ))}
          </div>
        </div>

        {/* Categories Section */}
        <div className="categories">
          <h2>Book Categories</h2>
          <div className="category-list">
            {categories.map((category, index) => (
              <span
                key={index}
                className="category"
                style={{
                  backgroundColor: "#D2691E",
                  color: "#FFFFFF",
                  padding: "5px 10px",
                  margin: "5px",
                  borderRadius: "5px",
                }}
              >
                {category}
              </span>
            ))}
          </div>
        </div>

        {/* Popular Books Section */}
        <div className="popular-books">
          <h2>Popular Books</h2>
          <div className="book-list">
            {popularBooks.map((book) => (
              <div key={book.id} className="book-card">
                <img
                  src={book.volumeInfo.imageLinks?.thumbnail}
                  alt={book.volumeInfo.title}
                  className="book-image"
                />
                <h3>{book.volumeInfo.title}</h3>
                <p>{book.volumeInfo.authors?.join(", ")}</p>
                <button
                  className={`favorite-button ${
                    favorites.has(book.id) ? "added" : ""
                  }`}
                  onClick={() => handleAddToFavorites(book)}
                  style={{
                    backgroundColor: favorites.has(book.id)
                      ? "#8B4513"
                      : "#C19A6B",
                    color: "#FFFFFF",
                  }}
                >
                  {favorites.has(book.id)
                    ? "Added to Favorites"
                    : "Add to Favorites"}
                </button>
              </div>
            ))}
          </div>
        </div>

        {/* Search Results Section */}
        {searchQuery && searchResults.length > 0 && (
          <div className="search-results">
            <h2>Search Results</h2>
            <div className="book-list">
              {searchResults.map((book) => (
                <div key={book.id} className="book-card">
                  <img
                    src={book.volumeInfo.imageLinks?.thumbnail}
                    alt={book.volumeInfo.title}
                    className="book-image"
                  />
                  <h3>{book.volumeInfo.title}</h3>
                  <p>{book.volumeInfo.authors?.join(", ")}</p>
                  <button
                    className={`favorite-button ${
                      favorites.has(book.id) ? "added" : ""
                    }`}
                    onClick={() => handleAddToFavorites(book)}
                    style={{
                      backgroundColor: favorites.has(book.id)
                        ? "#8B4513"
                        : "#C19A6B",
                      color: "#FFFFFF",
                    }}
                  >
                    {favorites.has(book.id)
                      ? "Added to Favorites"
                      : "Add to Favorites"}
                  </button>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Show message if no results found */}
        {searchQuery && searchResults.length === 0 && (
          <p>No books found for "{searchQuery}". Please try a different search.</p>
        )}
      </div>
      <Footer />
    </div>
  );
};

export default Home;
