import React, { useState, useEffect } from "react";
import axios from "axios";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import Slider from "react-slick"; // For the carousel

import '../CSS/UserHome.css'; // Custom CSS file for styling

const Home = () => {
    const [searchQuery, setSearchQuery] = useState("");  
    const [searchResults, setSearchResults] = useState([]);  
    const [popularBooks, setPopularBooks] = useState([]);  
    const [categories, setCategories] = useState([]);  
    const [loading, setLoading] = useState(false);  
    const [favorites, setFavorites] = useState(new Set());  

    const GOOGLE_BOOKS_API_KEY = 'AIzaSyBYNIOQ0-2vwqQSUEntsng8EGa0L5FaVg0';  

    useEffect(() => {
        setLoading(true);

        // Fetch popular books
        axios
            .get(`https://www.googleapis.com/books/v1/volumes?q=best+selling&key=${GOOGLE_BOOKS_API_KEY}`)
            .then((response) => setPopularBooks(response.data.items))
            .catch((error) => console.error("Error fetching popular books:", error))
            .finally(() => setLoading(false));

        // Fetch categories
        axios
            .get(`https://www.googleapis.com/books/v1/volumes?q=subject:fiction&key=${GOOGLE_BOOKS_API_KEY}`)
            .then((response) => {
                setCategories(response.data.items.map(item => item.volumeInfo.categories).flat());
            })
            .catch((error) => console.error("Error fetching categories:", error));
    }, []);

    const handleSearch = (e) => {
        e.preventDefault();
        if (searchQuery.trim() === "") return;

        setLoading(true);
        setSearchResults([]);  
        axios
            .get(`https://www.googleapis.com/books/v1/volumes?q=${searchQuery}&key=${GOOGLE_BOOKS_API_KEY}`)
            .then((response) => setSearchResults(response.data.items))
            .catch((error) => console.error("Error searching books:", error))
            .finally(() => setLoading(false));
    };

    const handleAddToFavorites = (book) => {
        const newFavorites = new Set(favorites);
        if (newFavorites.has(book.id)) {
            newFavorites.delete(book.id);
        } else {
            newFavorites.add(book.id);
        }
        setFavorites(newFavorites);
    };

    // Carousel settings for auto-sliding cards
    const carouselSettings = {
        infinite: true,
        slidesToShow: 3,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 3000,
        arrows: false,
        responsive: [
            {
                breakpoint: 768,
                settings: {
                    slidesToShow: 2,
                },
            },
            {
                breakpoint: 480,
                settings: {
                    slidesToShow: 1,
                },
            },
        ],
    };

    return (
        <div className="home">
            <Navbar />
            <div className="content">
                <h1 className="title">Welcome to Book Store</h1>
                
                <form onSubmit={handleSearch} className="search-form">
                    <input
                        type="text"
                        placeholder="Search books by title, author, or genre..."
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                        className="search-input"
                    />
                    <button type="submit" className="search-button" disabled={loading}>
                        {loading ? "Searching..." : "Search"}
                    </button>
                </form>

                {loading && <div className="loading">Loading...</div>}

                {searchQuery && searchResults.length > 0 && (
                    <div className="search-results">
                        <h2>Search Results</h2>
                        <div className="book-list">
                            {searchResults.map((book) => (
                                <div key={book.id} className="book-card">
                                    <img
                                        src={book.volumeInfo.imageLinks?.thumbnail}
                                        alt={book.volumeInfo.title}
                                        className="book-image"
                                    />
                                    <h3>{book.volumeInfo.title}</h3>
                                    <p>{book.volumeInfo.authors?.join(", ")}</p>
                                    <button
                                        className={`favorite-button ${favorites.has(book.id) ? "added" : ""}`}
                                        onClick={() => handleAddToFavorites(book)}
                                    >
                                        {favorites.has(book.id) ? "Added to Favorites" : "Add to Favorites"}
                                    </button>
                                </div>
                            ))}
                        </div>
                    </div>
                )}

                <div className="popular-books">
                    <h2>Popular Books</h2>
                    <Slider {...carouselSettings}>
                        {popularBooks.length > 0 ? (
                            popularBooks.map((book) => (
                                <div key={book.id} className="book-card">
                                    <img
                                        src={book.volumeInfo.imageLinks?.thumbnail}
                                        alt={book.volumeInfo.title}
                                        className="book-image"
                                    />
                                    <h3>{book.volumeInfo.title}</h3>
                                    <p>{book.volumeInfo.authors?.join(", ")}</p>
                                    <button
                                        className={`favorite-button ${favorites.has(book.id) ? "added" : ""}`}
                                        onClick={() => handleAddToFavorites(book)}
                                    >
                                        {favorites.has(book.id) ? "Added to Favorites" : "Add to Favorites"}
                                    </button>
                                </div>
                            ))
                        ) : (
                            <p>Loading popular books...</p>
                        )}
                    </Slider>
                </div>

                <div className="categories">
                    <h2>Book Categories</h2>
                    <div className="category-list">
                        {categories.length > 0 ? (
                            categories.map((category, index) => (
                                <span key={index} className="category">{category}</span>
                            ))
                        ) : (
                            <p>Loading categories...</p>
                        )}
                    </div>
                </div>

                {searchQuery && searchResults.length === 0 && (
                    <p>No books found for "{searchQuery}". Please try a different search.</p>
                )}
            </div>
            <Footer />
        </div>
    );
};

export default Home;
